import React, { Component } from "react";
import Arrow from "../../assets/icons/SVG/Icon-arrow-right.svg";
const content = {
  warehouse: "Warehouse Number 1",
  address: "469 King St W, Toronto, ON",
  contact: "Mara Weinberg",
  role: "Warehouse Manager",
  phone: "+1 416 678 2345",
  email: "weinberg@instack.com",
  categories: [
    "Industrial",
    "Automotive",
    "Heavy",
    "Mechanical",
    "Engineering",
    "Transportation"
  ]
};

function categoryLayout() {
  let lineOneContent =
    content.categories[0] +
    content.categories[1] +
    content.categories[2] +
    content.categories[3];

  let lineTwoContent = content.categories[4] + content.categories[5];

  let lineOne = (
    <div className="location__content-detail--categories--lineOne">
      {lineOneContent}
    </div>
  );
  let lineTwo = (
    <div className="location__content-detail--categories--lineTwo">
      {lineTwoContent}
    </div>
  );

  return (
    <div className="location__content-detail--categories">
      {lineOne}
      {lineTwo}
    </div>
  );
}
export default class locationDetail extends Component {
  render() {
    return (
      <div className="location__content-detail">
        <div className="location__content-detail--left">
          <div className="location__content-detail--geo">
            <div className="location__content-detail--geo--warehouse">
              {content.warehouse}
            </div>
            <div className="location__content-detail--geo--address">
              {content.address}
            </div>
          </div>

          <div className="location__content-detail--manager">
            <div className="location__content-detail--manager--name">
              {content.contact}
            </div>
            <div className="location__content-detail--manager--role">
              {content.role}
            </div>
          </div>

          <div className="location__content-detail--contact">
            <div className="location__content-detail--contact--phone">
              {content.phone}
            </div>
            <div className="location__content-detail--contact--email">
              {content.email}
            </div>
          </div>

          {categoryLayout()}
        </div>

        <div className="location__content-detail--right">
          <div className="location__content-detail--arrow">
            <img
              className="location__content-detail--arrow--icon"
              src={Arrow}
              alt="Right Arrow"
            ></img>
          </div>
        </div>
      </div>
    );
  }
}
